mapper.select:
select video_seq, video_id, video_title, video_channel_name, video_duration, video_view_cnt, video_cal, created_at, updated_at , (select count(*) from wish w where w.video_seq = v.video_seq and w.is_valid = 1) as video_wish_cnt , (select count(*) from comment c where c.video_seq = v.video_seq and c.is_valid = 1) as video_comment_cnt , (select workout_super_type_type from workout_super_type wsuper where wsuper.workout_super_type_seq = (select workout_super_type_seq from video_super_type vsuper where vsuper.video_seq = v.video_seq)
and workout_super_type_type = #{superType}
) as super_type , (select workout_sub_type_type from workout_sub_type wsub where wsub.workout_sub_type_seq = (select workout_sub_type_seq from video_sub_type vsub where vsub.video_seq = v.video_seq)
and workout_sub_type_type = #{subType}
) as sub_type from video v where v.is_valid = 1
and ${key} like concat('%', #{word}, '%')
ORDER BY ${sortDir}

SELECT workout_super_type_seq
FROM workout_super_type
WHERE workout_super_type_type = #{superType}

SELECT workout_sub_type_seq
FROM workout_sub_type
WHERE workout_sub_type_type = #{subType}
