mapper.select:
select video_seq, video_id, video_title, video_channel_name, video_duration, video_view_cnt, video_cal, created_at, updated_at , (select count(*) from wish w where w.video_seq = v.video_seq and w.is_valid = 1) as video_wish_cnt , (select count(*) from comment c where c.video_seq = v.video_seq and c.is_valid = 1) as video_comment_cnt from video v where v.is_valid = 1
and ${key} like concat('%', #{word}, '%')
ORDER BY ${sortDir}

mapper.update:
UPDATE VIDEO
SET video_view_cnt = video_view_cnt + 1
WHERE video_seq = #{videoSeq}

UPDATE WISH
SET is_valid = false
WHERE video_seq = #{videoSeq} AND user_seq = #{userSeq}
